import React, { useState } from 'react';
import { FaGithub, FaLink } from 'react-icons/fa';
import projectData from '../data/projectdata';

function ProjectsSection() {
  const [activeProject, setActiveProject] = useState(null);

  const toggleDetails = (id) => {
    setActiveProject(activeProject === id ? null : id);
  };

  return (
    <section id="projects" className="container mt-5">
      <h2 className="text-center mb-4">Projects</h2>
      <div className="row">
        {projectData.map(item => (
          <div key={item.id} className="col-md-6 col-lg-4 mb-4">
            <div className="card h-100 project-card" onClick={() => toggleDetails(item.id)}>
              <div className="card-body">
                <h5 className="card-title">{item.title}</h5>
                {activeProject === item.id && (
                  <div className="project-details">
                    <div className="tech-stack">{item.tech}</div>
                    <div className="project-links">
                      <a href={item.github_url} className="project-btn" target="_blank" rel="noopener noreferrer">
                        <FaGithub /> {/* GitHub Icon */}
                      </a>
                      {item.live_url && (
                        <a href={item.live_url} className="project-btn" target="_blank" rel="noopener noreferrer">
                          <FaLink /> {/* External Link Icon */}
                        </a>
                      )}
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default ProjectsSection;
