import React, { useState } from 'react';
import ParticlesBackground from './ParticlesBackground';

function HomeSection() {
  const [showParticles, setShowParticles] = useState(true);

  const toggleParticles = () => setShowParticles(!showParticles);

  return (
    <section id="home" className="container mt-5" style={{ position: 'relative', height: '100vh' }}>
      {showParticles && <ParticlesBackground />}
      <div className="row align-items-center" style={{ position: 'relative', zIndex: 2 }}>
        <div className="col-md-6">
          <h1>Hi, I'm Param Desai</h1>
          <p>I'm a full stack engineer and a software developer with a passion to learn Artificial Intelligence and Machine Learning</p>
          <button onClick={toggleParticles} className="btn btn-primary">
            {showParticles ? 'Hide Particles' : 'Show Particles'}
          </button>
        </div>
      </div>
    </section>
  );
}

export default HomeSection;
