import React from 'react';
import { Parallax } from 'react-parallax';
import useScrollRotation from '../data/useScrollRotation'; // Import the custom hook
import gearImage from '../images/gear.png'; // Update the path to your gear image

const ParallaxBackground = ({ children }) => {
  const rotation = useScrollRotation();

  // Define the style for the rotating background
  const backgroundStyle = {
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
    transform: `rotate(${rotation}deg)`, // Apply the rotation
  };

  return (
    <Parallax bgImage={gearImage} strength={500} bgStyle={backgroundStyle}>
      <div style={{ height: '100vh' }}>
        {children}
      </div>
    </Parallax>
  );
};

export default ParallaxBackground;
