import { useState, useEffect } from 'react';

const useScrollRotation = () => {
  const [rotation, setRotation] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      // Use window.scrollY to get the vertical scroll position and calculate rotation
      setRotation(window.scrollY % 360); // Modulo 360 to keep the rotation degree within 0-359
    };

    // Add event listener for the 'scroll' event
    window.addEventListener('scroll', handleScroll);

    // Remove the event listener in the cleanup function
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return rotation;
};

export default useScrollRotation;
