import React from 'react';
import { Parallax } from 'react-parallax';
import useScrollRotation from '../data/useScrollRotation.js'; // Import the custom hook
import gearImage from '../images/gear.png'; // Update the path to your gear image

const ParallaxBackground = ({ children }) => {
    const gearStyle = useScrollRotation();
  
    return (
      <Parallax strength={500}>
        {children}
        <Background className="custom-bg">
          <div style={{ width: '100%', height: '100%', position: 'relative', overflow: 'hidden' }}>
            <img src={gearImage} alt="Rotating Gear" style={gearStyle} />
          </div>
        </Background>
      </Parallax>
    );
  };
  
  export default ParallaxBackground;
