import React, { useState } from 'react';
import { FaGithub, FaExternalLinkAlt } from 'react-icons/fa';

function ProjectCard({ project }) {
  const [isFlipped, setIsFlipped] = useState(false);

  const handleFlip = () => {
    setIsFlipped(!isFlipped);
  };

  return (
    <div className={`card-container ${isFlipped ? 'is-flipped' : ''}`}>
      <div className="project-card">
        {/* Front of the card */}
        <div className="card-front">
          {/* ... card front content ... */}
          {/* Include the toggle switch here */}
          <label className="toggle-switch" onClick={handleFlip}>
            <input type="checkbox" checked={isFlipped} readOnly />
            <span className="switch" />
          </label>
        </div>
        {/* Back of the card */}
        <div className="card-back">
          {/* ... card back content ... */}
          {/* Include the toggle switch here as well */}
          <label className="toggle-switch" onClick={handleFlip}>
            <input type="checkbox" checked={isFlipped} readOnly />
            <span className="switch" />
          </label>
        </div>
      </div>
    </div>
  );
}

export default ProjectCard;
