import React from 'react';
// import ParticlesBackground from './ParticlesBackground'; // Import the ParticlesBackground component

function HomeSection() {
  return (
    <section id="home" className="container mt-5">
      <div className="row align-items-center">
        <div className="col-md-6">
          <h1>Hi, I'm Param Desai</h1>
          <p>I'm a full stack engineer and a software developer with a passion to learn Artificial Intelligence and Machine Learning</p>
        </div>
        <div className="col-md-6">
          <img src="../images/gear.png" alt="Param Desai" className="profile-image" />
        </div>
        <ParticlesBackground />
      </div>
    </section>
  );
}

export default HomeSection;
