import React from 'react';
import Particles from 'react-tsparticles';
import faceImage from '../images/gear.png'; // Correct file extension and path
import jsImage from '../images/js.png'; // Correct file extension and path

const ParticlesBackground = () => {
  return (
    <Particles
      id="tsparticles"
      options={{
        particles: {
          number: {
            value: 10, // Adjust the number of icons
          },
          shape: {
            type: 'image',
            image: [
              { src: faceImage, width: 100, height: 100 }, // Use the imported variables
              { src: jsImage, width: 100, height: 100 },
              // ... add other icons
            ],
          },
          size: {
            value: 20, // Adjust icon size
          },
          move: {
            speed: 2, // Adjust movement speed
          },
        },
        // If you want the particles to be interactive (respond to mouse hover, etc.), add interactivity options here
      }}
    />
  );
};

export default ParticlesBackground;
