import React, { useState } from 'react';
import { FaGithub, FaExternalLinkAlt } from 'react-icons/fa';
import './ToggleSwitch.css'; // Make sure to import the CSS for the toggle

function ProjectCard({ project }) {
  const [isFlipped, setIsFlipped] = useState(false);

  return (
    <div className={`card-container ${isFlipped ? 'is-flipped' : ''}`}>
      <div className="project-card">
        <div className="card-front">
          <h5 className="card-title">{project.title}</h5>
          <div className="project-links">
            <a href={project.github_url} className="project-btn">
              <FaGithub /> {/* GitHub Icon */}
            </a>
            {project.live_url && (
              <a href={project.live_url} className="project-btn">
                <FaExternalLinkAlt /> {/* External Link Icon */}
              </a>
            )}
          </div>
          {/* Include the toggle switch here */}
          <label className="toggle-switch">
            <input type="checkbox" checked={isFlipped} onChange={() => setIsFlipped(!isFlipped)} />
            <span className="switch" />
          </label>
        </div>
        <div className="card-back">
          <div className="tech-stack">{project.tech}</div>
        </div>
      </div>
    </div>
  );
}

export default ProjectCard;
